void fdisplay_cyc_term(fl, t, tsiz, dict)
     FILE *fl;
     Term t;
     int tsiz;
     struct ptrdict *dict;
{
    int	i;
    register Term tmp;
    long dist;

    if(Eq(t, NullTerm)) {
      fprintf(fl, "NULL");
      return;
    }

    tmp = t;
    DerefSwitchTerm(tmp,varlbl,snlbl,atmlbl,bnlbl,fltlbl,lstlbl,strlbl,genlbl);
 varlbl:
    display_variable(fl, tmp, tsiz, dict);
    goto exitlbl;
 snlbl:
    fprintf(fl,"%d",GetSmall(tmp));
    goto exitlbl;
 bnlbl:
    bignum_print(fl, Big(tmp));
    goto exitlbl;
 fltlbl:
    fprintf(fl,"%E",FltVal(Flt(tmp)));
    goto exitlbl;
 atmlbl:
    fprintf(fl,"%s",AtmPname(Atm(tmp)));
    goto exitlbl;
 lstlbl:
    dist = lookup_dict(dict, tmp);
    if (dist > 0) {
      fprintf(fl,"^%ld",dist);
      goto exitlbl;
    }

    if(tsiz == 0) {
      fprintf(fl,"[...]");
      goto exitlbl;
    }

    fprintf(fl,"[");
    {
      Term a;
      push_dict(dict,tmp);
      GetLstCar(a,Lst(tmp));
      fdisplay_cyc_term(fl,a,tsiz-1,dict);
      GetLstCdr(a,Lst(tmp));
      Deref(a,a);
      fdisplay_cyc_list(fl, a, tsiz, tsiz, dict);
      pop_dict(dict,tmp);
    }
    fprintf(fl,"]");
    goto exitlbl;
  strlbl:	    
    dist = lookup_dict(dict, tmp);
    if (dist > 0) {
      fprintf(fl,"^%ld",dist);
      goto exitlbl;
    }
    fprintf(fl,"%s(", AtmPname(StrName(Str(tmp))));
    if(tsiz == 0) {
      fprintf(fl,"...)");
      goto exitlbl;
    }
    push_dict(dict,tmp);
    if (FnkFeatures(StrFunctor(Str(tmp))) != 0) {
      
      Term *names = FnkNames(StrFunctor(Str(tmp)));
      Term a;

      for(i=0 ; i != FnkNums(StrFunctor(Str(tmp))); i++) {
	fprintf(fl,"%d",i+1);
	fprintf(fl,":");
	GetStrArg(a,Str(tmp),i);
	fdisplay_cyc_term(fl,a,tsiz-1,dict);
	if(i != (StrArity(Str(tmp)) - 1))
	  fprintf(fl,",");
      }
      for(i=0 ; i != FnkFeatures(StrFunctor(Str(tmp))); i++) {
	a = names[i];
	fdisplay_cyc_term(fl,a,tsiz-1,dict);
	fprintf(fl,":");
	GetStrArg(a,Str(tmp),i+FnkNums(StrFunctor(Str(tmp))));
	fdisplay_cyc_term(fl,a,tsiz-1,dict);
	if(i != (StrArity(Str(tmp)) - 1))
	  fprintf(fl,",");
      }
    } else {
      for(i=0 ; i != StrArity(Str(tmp)); i++) {
	Term a;
	GetStrArg(a,Str(tmp),i);
	fdisplay_cyc_term(fl,a,tsiz-1,dict);
	if(i != (StrArity(Str(tmp)) - 1))
	  fprintf(fl,",");
      }
    }
    pop_dict(dict,tmp);
    fprintf(fl,")");
    goto exitlbl;
  genlbl:
    dist = lookup_dict(dict, tmp);
    if (dist > 0) {
      fprintf(fl,"^%ld",dist);
      goto exitlbl;
    }

    push_dict(dict,tmp);
    if (!(Gen(tmp)->method->print(fl,Gen(tmp),tsiz-1,dict)))
      fprintf(fl, "<unknown generic:%#lx>", (long)Gen(tmp));
    pop_dict(dict,tmp);
    goto exitlbl;

  exitlbl:
    return;
}


#define CYC_LIST_STACK_SIZE 10

void fdisplay_cyc_list(fl, x, tsiz, tsiz1, dict)
     FILE *fl;
     Term x;
     int tsiz;
     int tsiz1;
     struct ptrdict *dict;
{
  Term a;
  Term stack[CYC_LIST_STACK_SIZE];
  int i = 0;
  long dist;

  dist = lookup_dict(dict, x);
  if (dist > 0) {
    fprintf(fl,"|^%ld",dist);
    return;
  }

  while(IsLST(x) && (--tsiz1 != 0)) {
    push_dict(dict,x);
    stack[i++] = x;

    fprintf(fl,",");
    GetLstCar(a,Lst(x));
    fdisplay_cyc_term(fl,a,tsiz-1,dict);
    GetLstCdr(x,Lst(x));
    Deref(x,x);

    if (i == CYC_LIST_STACK_SIZE) {
      fdisplay_cyc_list(fl,x,tsiz,tsiz1,dict);
      goto unwind;
    }
    dist = lookup_dict(dict, x);
    if (dist > 0) {
      fprintf(fl,"|^%ld",dist);
      goto unwind;
    }
  }

  if(tsiz1 == 0)
    fprintf(fl,",...");
  else if(!Eq(x, NIL)) {
    fprintf(fl,"|");
    fdisplay_cyc_term(fl,x,tsiz-1);
  }

 unwind:

  while (i-- != 0)
    pop_dict(dict,stack[i]);

  return;
}


