void display_a_continuations(cont,ind)
    andcont *cont;
     int ind;
{    

#ifdef TRACE
char *name;
int arity;  
if(cont==NULL) return;
if(cont->def==NULL) {
  name="Root";
  arity = 0;
} else {
  name=AtmPname(cont->def->name);
  arity = cont->def->arity;
}
#endif
    if(cont->next==NULL) return;

#ifdef TRACE
    if(cont->trace & TRACE_INIT) 
      {
	printf("\n");
	indent(ind);
	printf("<< top level >>");
	return;
      }
#endif

    if(cont->label==NULL) 
      {
	display_a_continuations(cont->next,ind);
	return;
      }

    switch(CodeToEnum(GetOpCode(cont->label))) {
    case GUARD_COND:
    case GUARD_COMMIT:
    case GUARD_CUT:
    case GUARD_WAIT:
    case GUARD_UNORDER:
    case GUARD_ORDER:
    case GUARD_UNIT:
    case GUARD_HALT:
      printf("\n");
      indent(ind);
      printf("%% ");
#ifdef TRACE
      printf("<<");
      display_cont_trace(cont);
      if(trace_full_cont) 
	{
	  printf("%d:%s",AndContNo(cont),name);
	  break;
	}
      else
	{
	  printf("%s/%d/%d>>",name,arity,AndContNo(cont));
	  display_a_continuations(cont->next,ind);
	  return;
	}
#else
      printf("<<>>");
      display_a_continuations(cont->next,ind);
      return;
#endif
    case PROCEED:
      printf("\n");
      indent(ind);
#ifdef TRACE
      printf("<<<");
      display_cont_trace(cont);
      if(trace_full_cont) 
	{
	  printf("%d:%s",AndContNo(cont),name);
	  break;
	}
      else
	{
	  printf("%s/%d/%d>>",name,arity,AndContNo(cont));
	  display_a_continuations(cont->next,ind);
	  return;
	}
#else
      printf("<<>>");
      display_a_continuations(cont->next,ind);
      return;
#endif
      
    default:
      printf("\n");
      indent(ind);
#ifdef TRACE
      printf("<<");
      display_cont_trace(cont);
      if(trace_full_cont) 
	{
	  printf("%d:%s",AndContNo(cont),name);
	  break;
	}
      else
	{
	  printf("%s/%d/%d>>",name,arity,AndContNo(cont));
	  display_a_continuations(cont->next,ind);
	  return;
	}
#else
      printf("<<>>");
      display_a_continuations(cont->next,ind);
      return;
#endif

    }
#ifdef TRACE
if(cont->def!=NULL)
  display_cont_args(cont);
printf(")>>");
display_a_continuations(cont->next,ind);
#endif
}

